<script>
import { kebabCase } from 'lodash'
export default {
    async beforeCreate() {
        this.$store.dispatch('fetchInventory')
    },
    computed: {
        tyreDetails() {
            return this.$store?.state?.inventory?.filter(tyre => kebabCase(tyre.stock_item) == this.$route.params.slug)[0];
        },
        similarTyres() {
            return this.$store.state.inventory.slice(0, 2)
        }
    },
}
